sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var requestUserName = request.pathParams.user_name;
	var requestQuery = request.queryParams.ni_query;

	if(!requestUserName.user_name){
		var apiError = new sn_ws_err.ServiceError();
		apiError.setStatus(542);
		apiError.setMessage("User not found");
		apiError.setDetail("No user record found for the user_name passed into the ni_info web service resource using the user_name path parameter.");
		response.setError(apiError);
		return;
	}
	var requestUser = new GlideRecord('sys_user');
	requestUser.get('user_name', requestUserName);

	var userNICount = new GlideAggregate('x_58872_needit_needit');
	userNICount.addAggregate('COUNT');
	userNICount.addQuery('u_requested_for', requestUser.sys_id);
	userNICount.addEncodedQuery(requestQuery);
	userNICount.groupBy('u_request_type');
	userNICount.query();

	var needItRecs = 0;
	var reqType = '';
	var needItSummary = [];
	var needItRecCount = 0;

	while(userNICount.next()) {
		needItRecs = userNICount.getAggregate('COUNT');
		reqType = userNICount.u_request_type.getDisplayValue();
		needItSummary.push({"requestType": reqType, "count": needItRecs});
		needItRecCount = parseInt(needItRecCount) + parseInt(needItRecs);
	}

	var body = {};
	body.totalUserNICount = needItRecCount;
	body.catcount = needItSummary;
	body.user = {"User Name": requestUser.user_name, "User ID": requestUser.sys_id};

	response.setBody(body);

})(request, response);
